<div id="facebook_new" class="tokyo_tm_section">
    <div class="box_inner">
        <div class="description_wrap">
            <div class="image">
                <!-- <img src="home/assets/images/slider/elderly-technology.jpg" alt=""> -->
            </div>
            <div class="details">
                <div class="extra">
                    <div class="short custom_size">
                        <p class="date">By <a href="#">Alex Watson</a> <span>05 April 2021</span></p>
                    </div>
                    <div class="my_like custom_size">
                        <a href="#"><img class="svg" src="home/assets/images/svg/like.svg" alt=""><span>35</span></a>
